import React, { useState, useEffect } from 'react';
import { getAuth, signInWithPopup, GoogleAuthProvider, onAuthStateChanged, signOut } from 'firebase/auth';
import UploadSection from './UploadSection';
import { app } from '../firebaseConfig';
import { Button, MenuItem, Select, InputLabel, FormControl } from '@mui/material';
import { Cloudinary } from 'cloudinary-core';
import './UploadModal.css'; // <-- Add this import

const UploadModal = ({ closeModal }) => {
  const [user, setUser] = useState(null);
  const [selectedOption, setSelectedOption] = useState('');
  const [file, setFile] = useState(null);
  const [metadata, setMetadata] = useState({ artist: '', title: '' });
  const auth = getAuth(app);
  const provider = new GoogleAuthProvider();

  const cloudinary = new Cloudinary({ cloud_name: 'your_cloud_name', secure: true });

  const handleGoogleSignIn = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
    } catch (error) {
      console.error('Google sign-in error:', error);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      setUser(null);
    } catch (error) {
      console.error('Logout error:', error);
    }
  };

  const handleSelectChange = (event) => {
    setSelectedOption(event.target.value);
  };

  const handleFileChange = async (event) => {
    const file = event.target.files[0];
    setFile(file);

    // Upload file to Cloudinary
    const formData = new FormData();
    formData.append('file', file);
    formData.append('upload_preset', 'your_upload_preset');

    try {
      const response = await fetch(`https://api.cloudinary.com/v1_1/your_cloud_name/upload`, {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();

      // Fetch metadata from Cloudinary
      const publicId = data.public_id;
      const metadataResponse = await fetch(`https://api.cloudinary.com/v1_1/your_cloud_name/resources/video/upload/${publicId}`, {
        method: 'GET',
        headers: {
          'Authorization': `Basic ${btoa('your_api_key:your_api_secret')}`,
        },
      });
      const metadata = await metadataResponse.json();
      setMetadata({ artist: metadata.context.custom.artist, title: metadata.context.custom.title });
    } catch (error) {
      console.error('Error uploading file or fetching metadata:', error);
    }
  };

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      if (currentUser) {
        setUser(currentUser);
      }
    });
    return () => unsubscribe();
  }, [auth]);

  return (
    <div className="modal">
      <div className="upload-modal">
        {user ? (
          <>
            <div className="user-info">
              {user.photoURL ? (
                <img src={user.photoURL} alt="Profile" className="profile-image" />
              ) : (
                <p>No profile image available</p>
              )}
              <Button variant="contained" color="secondary" onClick={handleLogout}>
                Logout
              </Button>
            </div>
            <FormControl fullWidth variant="outlined" margin="normal">
              <InputLabel id="select-label">Playlist Type</InputLabel>
              <Select
                labelId="select-label"
                value={selectedOption}
                onChange={handleSelectChange}
                label="Playlist Type"
              >
                <MenuItem value="">
                  <em>None</em>
                </MenuItem>
                <MenuItem value={10}>Option 1</MenuItem>
                <MenuItem value={20}>Option 2</MenuItem>
                <MenuItem value={30}>Option 3</MenuItem>
              </Select>
            </FormControl>
            <Button
              variant="contained"
              component="label"
              color="primary"
              style={{ marginTop: '20px' }}
            >
              Upload MP3
              <input
                type="file"
                accept="audio/mp3"
                hidden
                onChange={handleFileChange}
              />
            </Button>
            {metadata.artist && metadata.title && (
              <div className="metadata">
                <p>Artist: {metadata.artist}</p>
                <p>Title: {metadata.title}</p>
              </div>
            )}
            <UploadSection currentUserEmail={user.email} />
          </>
        ) : (
          <>
            <h2>Sign in with Google to upload</h2>
            <Button variant="contained" color="primary" onClick={handleGoogleSignIn}>
              Sign in with Google
            </Button>
          </>
        )}
        <Button variant="contained" color="primary" onClick={closeModal} style={{ marginTop: '20px' }}>
          Back to the Station
        </Button>
      </div>
    </div>
  );
};

export default UploadModal;
