import React, { useState, useEffect } from 'react';
import { getAuth, signInWithPopup, GoogleAuthProvider, onAuthStateChanged, signOut } from 'firebase/auth';
import UploadSection from './UploadSection';
import { app } from '../firebaseConfig';
import { Button, MenuItem, Select, InputLabel, FormControl } from '@mui/material';
import './UploadModal.css'; // <-- Add this import

const UploadModal = ({ closeModal }) => {
  const [user, setUser] = useState(null);
  const [selectedOption, setSelectedOption] = useState('');
  const auth = getAuth(app);
  const provider = new GoogleAuthProvider();

  const handleGoogleSignIn = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
    } catch (error) {
      console.error('Google sign-in error:', error);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      setUser(null);
    } catch (error) {
      console.error('Logout error:', error);
    }
  };

  const handleSelectChange = (event) => {
    setSelectedOption(event.target.value);
  };

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      if (currentUser) {
        setUser(currentUser);
      }
    });
    return () => unsubscribe();
  }, [auth]);

  return (
    <div className="upload-modal">
      {user ? (
        <>
          <div className="user-info">
            {user.photoURL ? (
              <img src={user.photoURL} alt="Profile" className="profile-image" />
            ) : (
              <p>No profile image available</p>
            )}
            <Button variant="contained" color="secondary" onClick={handleLogout}>
              Logout
            </Button>
          </div>
          <FormControl fullWidth variant="outlined" margin="normal">
            <InputLabel id="select-label">Select Option</InputLabel>
            <Select
              labelId="select-label"
              value={selectedOption}
              onChange={handleSelectChange}
              label="Select Option"
            >
              <MenuItem value="">
                <em>None</em>
              </MenuItem>
              <MenuItem value={10}>Option 1</MenuItem>
              <MenuItem value={20}>Option 2</MenuItem>
              <MenuItem value={30}>Option 3</MenuItem>
            </Select>
          </FormControl>
          <UploadSection currentUserEmail={user.email} />
        </>
      ) : (
        <>
          <h2>Sign in with Google to upload</h2>
          <Button variant="contained" color="primary" onClick={handleGoogleSignIn}>
            Sign in with Google
          </Button>
        </>
      )}
      <Button variant="contained" color="primary" onClick={closeModal}>
        Back to the Station
      </Button>
    </div>
  );
};

export default UploadModal;
