import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import UploadModal from './UploadModal';
import { Button } from '@mui/material';
import { PlayArrow, Pause } from '@mui/icons-material';
import bg from '../assets/bg.png'; // Place bg.png inside /src/assets
import './RadioPlayer.css'; // <-- Add this import

Modal.setAppElement('#root'); // Required for accessibility

const RadioPlayer = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [audio, setAudio] = useState(null);
  const [isUploadModalOpen, setIsUploadModalOpen] = useState(false);
  const [tracks, setTracks] = useState([]);
  const [currentTrackIndex, setCurrentTrackIndex] = useState(null);
  const [nowPlaying, setNowPlaying] = useState({ artist: '', title: '', playlist: '' });

  useEffect(() => {
    const fetchPlaylists = async () => {
      try {
        const res = await fetch('http://localhost:5001/api/cloudinary/playlists');
        if (!res.ok) {
          throw new Error(`HTTP error! status: ${res.status}`);
        }
        const data = await res.json();
        setTracks(data.tracks);
      } catch (error) {
        console.error('Error fetching playlists:', error);
      }
    };

    fetchPlaylists();
  }, []);

  const playRandomTrack = () => {
    if (tracks.length > 0) {
      const randomIndex = Math.floor(Math.random() * tracks.length);
      const track = tracks[randomIndex];
      setCurrentTrackIndex(randomIndex);
      const newAudio = new Audio(track.url);
      newAudio.addEventListener('ended', handleTrackEnd);
      setAudio(newAudio);
      setNowPlaying({ artist: track.artist, title: track.title, playlist: track.playlist });
      setIsPlaying(true);
    }
  };

  const handleTrackEnd = () => {
    playRandomTrack();
  };

  useEffect(() => {
    if (audio) {
      if (isPlaying) {
        audio.play();
      } else {
        audio.pause();
      }
    }
  }, [isPlaying, audio]);

  const togglePlay = () => {
    if (!isPlaying) {
      playRandomTrack();
    } else {
      setIsPlaying(false);
    }
  };

  const openUploadModal = () => setIsUploadModalOpen(true);
  const closeUploadModal = () => setIsUploadModalOpen(false);

  return (
    <div className="radio-player" style={{ textAlign: 'center' }}>
      <header
        className="header"
        style={{
          background: "#4CAF50",
          color: "white",
          padding: "1rem",
        }}
      >
        <h1>Riff Raff Deciders Radio</h1>
      </header>
      <div
        className="hero"
        style={{
          background: `url(${bg}) no-repeat center center/cover`,
          height: "70vh",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          color: "white",
          padding: "0 20px",
        }}
      >
        <h2>WE DON'T DO RIFF RAFF.</h2>
        <p>Currated by great people from the West Coast to the East.</p>
        <div style={{ marginTop: "20px" }}>
          <div className="button-row">
            <Button
              variant="contained"
              color="primary"
              size="large"
              startIcon={isPlaying ? <Pause /> : <PlayArrow />}
              onClick={togglePlay}
            >
              {isPlaying ? 'Pause' : 'Play'}
            </Button>
          </div>
          <Button
            onClick={openUploadModal}
            style={{
              margin: "0 10px",
              padding: "10px 20px",
              fontSize: "1rem",
            }}
          >
            Upload Track
          </Button>
        </div>
      </div>
      <div className="now-playing" style={{ marginTop: "20px", color: "white" }}>
        <h3>Now Playing</h3>
        <h2>{nowPlaying.title}</h2>
        <h3>{nowPlaying.playlist}</h3>
        <p>{nowPlaying.artist}</p>
      </div>
      <div className="upcoming-shows">
        <h3>Upcoming Shows</h3>
        <p>10:00 PM - Sleepy Time</p>
        <p>8:00 AM - Morning</p>
        <p>12:00 PM - Chill</p>
        <p>4:00 PM - Get Pumped Up</p>
        <p>6:00 PM - Driving</p>
        <p>8:00 PM - Hanging out</p>
      </div>
      <Modal
        isOpen={isUploadModalOpen}
        onRequestClose={closeUploadModal}
        contentLabel="Upload Modal"
        className="modal"
        overlayClassName="overlay"
      >
        <UploadModal closeModal={closeUploadModal} />
      </Modal>
    </div>
  );
};

export default RadioPlayer;
