import React, { useState, useEffect } from 'react';
import { getAuth, signInWithPopup, GoogleAuthProvider, onAuthStateChanged, signOut } from 'firebase/auth';
import UploadSection from './UploadSection';
import { app } from '../firebaseConfig';

const UploadModal = ({ closeModal }) => {
  const [user, setUser] = useState(null);
  const auth = getAuth(app);
  const provider = new GoogleAuthProvider();

  const handleGoogleSignIn = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
    } catch (error) {
      console.error('Google sign-in error:', error);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      setUser(null);
    } catch (error) {
      console.error('Logout error:', error);
    }
  };

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      if (currentUser) {
        setUser(currentUser);
        console.log('User profile URL:', currentUser.photoURL); // Debugging line
      }
    });
    return () => unsubscribe();
  }, [auth]);

  return (
    <div className="upload-modal" style={{ textAlign: 'center', padding: '20px' }}>
      {user ? (
        <>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            {user.photoURL ? (
              <img src={user.photoURL} alt="Profile" style={{ borderRadius: '50%', width: '40px', height: '40px' }} />
            ) : (
              <p>No profile image available</p>
            )}
            <button
              onClick={handleLogout}
              style={{
                margin: '10px',
                padding: '10px 20px',
                fontSize: '1rem',
              }}
            >
              Logout
            </button>
          </div>
          <UploadSection currentUserEmail={user.email} />
        </>
      ) : (
        <>
          <h2>Sign in with Google to upload</h2>
          <button
            onClick={handleGoogleSignIn}
            style={{
              margin: '10px',
              padding: '10px 20px',
              fontSize: '1rem',
            }}
          >
            Sign in with Google
          </button>
        </>
      )}
      <button
        onClick={closeModal}
        style={{
          margin: '10px',
          padding: '10px 20px',
          fontSize: '1rem',
        }}
      >
        Back to the Station
      </button>
    </div>
  );
};

export default UploadModal;
